mapper.update:
update user
set name = #{name,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=INTEGER},
  age = #{age,jdbcType=INTEGER},
  phone = #{phone,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, name, gender, age, phone
from user
where user_id = #{userId,jdbcType=INTEGER}
